import React, { useRef } from 'react'
import { ICustomer } from './../interfaces';


interface CustomerFormProps {
    onAdd(title: ICustomer): void
} 

export const CustomerForm: React.FC<CustomerFormProps> = props =>
{
    const ref = useRef<HTMLInputElement>(null)

    const keyPressHandler = (event: React.KeyboardEvent) => {
        if(event.key === "Enter"){
            console.log(event.target.name);

            const newCustomer: ICustomer = {
                id: Date.now(),
                name : 'sds22asd',
                phone : 'asasdd'
            }

            props.onAdd(newCustomer);
        }
    }

    return (
        <div className="input-field mt2">
            <input 
            type="text" 
            id="name" 
            name="name"
            placeholder="Placeholder"
            ref = {ref}
            onKeyPress = {keyPressHandler} />
            <label htmlFor="name" className="active">Placeholder</label>

            <input 
            type="text" 
            id="dob" 
            name="dob"
            placeholder="Placeholder"
            ref = {ref}
            onKeyPress = {keyPressHandler} />
            <label htmlFor="dob" className="active">Placeholder</label>
        </div>
    )
}
